-- 根据已上课时间计算更新教练的喱喱教龄
-- 现约
UPDATE t_u_coach AS t1,
(SELECT IFNULL(SUM(TIMESTAMPDIFF(MINUTE, rstart, rend)),0)/60 sumTime, t2.`coachId` coachid FROM t_order AS t1 RIGHT JOIN t_u_coach AS t2 ON t1.`coach_id` = t2.`coachId`
WHERE t1.`otype` = 1 AND t1.`order_state` BETWEEN 4 AND 7 AND gtime > '2015-11-24'
GROUP BY t2.`coachId`) AS t2
SET t1.coachAge = sumTime WHERE t1.`coachid` = t2.`coachId`;
-- 预约
UPDATE t_u_coach AS t1,
(SELECT IFNULL(SUM(TIMESTAMPDIFF(MINUTE, rstart, rend)),0)/60 sumTime, t2.`coachId` FROM t_coach_class AS t1 RIGHT JOIN t_u_coach AS t2 ON t1.`coach_id` = t2.`coachId`
WHERE t1.`book_num` > 0 AND t1.`isdel` = 0 AND ccid IN
(SELECT ccid FROM t_order WHERE otype = 3 AND order_state BETWEEN 4 AND 7 AND gtime > '2015-11-24') 
GROUP BY t2.`coachId`) AS t2
SET t1.coachAge = sumTime + t1.coachAge WHERE t1.coachid = t2.coachId;


