UPDATE `t_u_student` AS t1, `t_enroll_order` AS t2 
SET 
t1.`name` = t2.`NAME`,
t1.`idNumber` = t2.`card_id`,
t1.`idPhotoFront` = t2.`card_pic1`,
t1.`idPhotoBack` = t2.`card_pic2`
WHERE t1.`studentId` = t2.`student_id` AND t2.`card_id` IS NOT NULL;

-- 更新报名订单，去除因异常生成的多余订单。
UPDATE t_enroll_order AS t1,
 (
	SELECT
		studentId,
		applyorder
	FROM
		t_u_student
	WHERE
		studentId IN (
			SELECT
				student_id
			FROM
				(
					SELECT
						*, count(student_id) AS ct
					FROM
						t_enroll_order
					WHERE
						isdel = 0
					GROUP BY
						student_id
					HAVING
						ct > 1
				) abc
		)
) AS t2
SET t1.isdel = 1
WHERE
	t1.student_id = t2.studentId
AND t1.order_id != t2.applyorder;



