mapper.select:
select
	,
	 (select money from t_s_schaccount a where s.school_id= a.schoolId)as accountMoney
from t_s_deposit s
	where s.school_id= #{schoolId,jdbcType=VARCHAR} 


				and s.check_status = #{checkStatus}


			   and date(s.apply_time)  between #{startTime} and #{endTime}


	order by s.id desc

update.foreach:
update t_s_deposit
transfer='${depositUpdate.transfer}', bankResTime=NOW(), transferTime =NOW(), prov='${depositUpdate.prov}', city='${depositUpdate.city}', response_code='${depositUpdate.responseCode}', merDate='${depositUpdate.merDate}', merSeqId='${depositUpdate.merSeqId}', stat='${depositUpdate.stat}', feeAmt=${depositUpdate.feeAmt}, check_status=${depositUpdate.checkStatus}
id=${depositUpdate.id}

             update t_s_deposit 

						checker='${depositUpdate.checker}',
						check_remark='${depositUpdate.checkRemark}',
						check_time =NOW(),
						check_status=${depositUpdate.checkStatus}


		       		 id=${depositUpdate.id}
